<?php$options = [    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,    PDO::ATTR_EMULATE_PREPARES   => false,];    $db = new PDO("mysql:host=localhost;dbname=register_user;charset=utf8",'root','',$options);//    $error_type = array(//    E_login => 'Введите логин',//    E_password => 'Введите пароль'//    );if(!isset($_POST['login'])){        echo  "Login33";        exit();    }else if(!isset($_POST['password'])){    echo "password";    exit();}else{    $login = $_POST['login'];    $password =md5( $_POST['password']);    $query="SELECT username, password FROM users WHERE username = ? AND password=?";    $stmt = $db->prepare($query);    $stmt->execute(array($login,$password));    $res = $stmt->fetchAll(PDO::FETCH_ASSOC);    if($res){        echo "You input correct data";    }    else{        echo "You input incorrect data";    }}?>